;;; {"type":"html","content":"<span class='clj-var'>#&#x27;kalman/plot-moments</span>","value":"#'kalman/plot-moments"}
;; <=

;; **
;;; ### Inferred Trajectories 
;;; 
;;; *blue*: Sample estimate
;;; *green*: Kalman smoother
;; **

;; @@
(plot/compose 
  (plot/list-plot (map #(into [] %) (:mean true-moments)) 
      	          :joined true
                  :plot-range [+x-limits+ +x-limits+]
        	      :color (nth +plot-colors+ 2))
  (plot/list-plot (map #(into [] %) (:mean moments)) 
      	          :joined true
                  :plot-range [+x-limits+ +x-limits+]
        	      :color (nth +plot-colors+ 0)))
